package phase1.practiceproblems;

public class StringsComparisonEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String t="Delhi";
		String o="Mumbai";
		String k="delhi";
		String y=new String("Mumbai");
		String l=new String("delhi");
		String p=new String("Hello");
		//compare o and l
		if(o.equals(l))
			System.out.println("True");
		else
			System.out.println("False");
		if(o==l)
			System.out.println("True");
		else
			System.out.println("False");
		//compare y and p
		if(y.equals(p))
			System.out.println("True");
		else
			System.out.println("False");
		if(y==p)
			System.out.println("True");
		else
			System.out.println("False");
		//compare t and o
		if(t.equals(o))
			System.out.println("True");
		else
			System.out.println("False");
		if(t==o)
			System.out.println("True");
		else
			System.out.println("False");
		//compare k and y
		if(k.equals(y))
			System.out.println("True");
		else
			System.out.println("False");
		if(k==y)
			System.out.println("True");
		else
			System.out.println("False");
		//compare p and y
		if(p.equals(y))
			System.out.println("True");
		else
			System.out.println("False");
		if(p==y)
			System.out.println("True");
		else
			System.out.println("False");
		
		
		
		
		
	}

}
